sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	var cindx = '';

	 c.selectItm = function(indx){
		 cindx = indx;
		var sysId = c.data.notes[indx].sys_id;
		 $rootScope.$emit('selectNote',sysId);
	};

	$rootScope.$on('UpdateTitle',function(event,data){
		c.data.notes[cindx].title = data;
	});
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
	data.notes = [];

	var gr = new GlideRecord('x_snc_createnotes_note');
	gs.info('user');
	gr.addQuery('user',gs.getUser().getID());
	gr.orderByDesc('sys_created_on');
	gr.query();
	while(gr.next()){
		var crObj = {};
		$sp.getRecordDisplayValues(crObj,gr,'number,title,sys_id');
		crObj.note = gr.getValue('note');
		data.notes.push(crObj);
	}

})();
