sp_widget.client_script:
api.controller=function() {
  /* widget controller */
  var c = this;
	$rootScope.$on('selectNote',function(event,data){
		c.server.get({
			action:'getNote',
			id:data

		}).then(function(r){
			c.data.note = r.data.note;
			c.data.title = r.data.title;
			c.data.noteId = r.data.sys_id;
		});
	});
	c.UpdateTitle = function(title){
		c.server.get({
			action:'updateNote',
			id:c.data.noteId,
			title:c.data.title,
			note: c.data.note
		}).then(function(r){
			var newTitle = r.data.title;
			console.log("New title is " + newTitle);
			$rootScope.$emit('UpdateTitle',newTitle);
		}
	)};
	c.UpdateDesc = function(title){
		c.server.get({
			action:'updateDesc',
			id:c.data.noteId,
			title:c.data.title,
			note: c.data.note
		}).then(function(r){}
	)};
};

sp_widget.script:
(function() {
	/* populate the 'data' object */
	/* e.g., data.table = $sp.getValue('table'); */
	if(input && input.id){
		var gr = new GlideRecord('x_snc_createnotes_note');
		gr.get(input.id);
		if(input.action === 'getNote'){
			$sp.getRecordDisplayValues(data,gr,'title,note,sys_id') 
		}
		else if (input.action === 'updateNote'){
			gr.title = input.title ;
			gr.update();
			$sp.getRecordDisplayValues(data,gr,'title,note,sys_id') ;
		}
		else if (input.action === 'updateDesc'){
			gr.note = input.note ;
			gr.update();
			$sp.getRecordDisplayValues(data,gr,'title,note,sys_id');
		}
	}

})();
